mapper.select:
select * from (select b.STNM,b.STCD,c.TM,c.RZ,c.CV,c.INQ,c.OTQ,c.DW,a.FWL,a.FWL79,c.Max_RZ,CONVERT(varchar(16), c.Max_TM, 120) as Max_TM,c.Min_RZ,CONVERT(varchar(16), c.Min_TM, 120) as Min_TM, (select FieldName from WRP_FieldInfo where FormID='ST_RSR_HYCH' and FieldID='RSVRTP' and Field=r.RSVRTP) as LEVEL, (select FieldName from WRP_FieldInfo where FormID='ST_STBPRP_B' and FieldID='STGR' and Field=b.STGR) as STGR, (select case ad_lv when 5 then AD_NM when 6 then (select AD_NM from WRP_AD_B sub where sub.ad_cd=m.UP_ADCD)+AD_NM end from WRP_AD_B m where AD_CD=ADDVCD) as AD_NM , ROW_NUMBER() OVER(Order by (b.STCD)) AS RowId from ST_STBPRP_B b inner join ST_STBPRP_F f on f.type=2 and b.stcd=f.stcd left join (select * from ST_RSVR_D where tm=cast(#{TM} as datetime) ) c on b.stcd=c.stcd left join ST_RSR_HYCH r on r.stcd=b.stcd left join ST_RSV_Alarm a on b.STCD=a.STCD
and
b.addvcd like #{ADDVCD}+'%'
and b.stcd in(select stcd from ST_STINFO_WRP where FormID='WRP_IrrBTCanalSystem' and (BaseID = #{Canal_Code} or BaseID in(select Canal_Code from dbo.fn_getCanalChild(#{Canal_Code}))))
and r.RSVRTP=#{LEVEL}
and b.stgr=#{STGR}
and b.stnm like '%'+#{STNM}+'%'
) as b

select * from (select b.STNM,b.STCD,convert(varchar,c.yr)+case when len(c.MON)=1 then '-0'+ convert(varchar,c.mon) else (select CONVERT(varchar,c.yr)+'-'+convert(varchar,c.mon)) end as DT,c.RZ,c.INQ,c.OTQ,c.MW,a.FWL,a.FWL79,c.Max_RZ,CONVERT(varchar(16), c.Max_TM, 120) as Max_TM,c.Min_RZ,CONVERT(varchar(16), c.Min_TM, 120) as Min_TM, (select FieldName from WRP_FieldInfo where FormID='ST_RSR_HYCH' and FieldID='RSVRTP' and Field=r.RSVRTP) as LEVEL, (select FieldName from WRP_FieldInfo where FormID='ST_STBPRP_B' and FieldID='STGR' and Field=b.STGR) as STGR, (select case ad_lv when 5 then AD_NM when 6 then (select AD_NM from WRP_AD_B sub where sub.ad_cd=m.UP_ADCD)+AD_NM end from WRP_AD_B m where AD_CD=ADDVCD) as AD_NM , ROW_NUMBER() OVER(Order by (b.STCD)) AS RowId from ST_STBPRP_B b inner join ST_STBPRP_F f on f.type=2 and b.stcd=f.stcd left join (select * from ST_RSVR_M where yr=#{YR} and mon=#{MON} ) c on b.stcd=c.stcd left join ST_RSR_HYCH r on r.stcd=b.stcd left join ST_RSV_Alarm a on b.STCD=a.STCD
and
b.addvcd like #{ADDVCD}+'%'
and b.stcd in(select stcd from ST_STINFO_WRP where FormID='WRP_IrrBTCanalSystem' and (BaseID = #{Canal_Code} or BaseID in(select Canal_Code from dbo.fn_getCanalChild(#{Canal_Code}))))
and r.RSVRTP=#{LEVEL}
and b.stgr=#{STGR}
and b.stnm like '%'+#{STNM}+'%'
) as b

select * from (select substring(CONVERT(varchar(100), TM, 120),1,20) as TM,RZ,INQ,OTQ,W, ROW_NUMBER() OVER(Order
by tm desc) AS RowId
from ST_RSVR_R


        STCD = #{STCD}


        and tm >='${Time_min}' and tm <='${Time_max}'


) as b
