mapper.select:
select * from (select STNM,(select FieldName from WRP_FieldInfo where FormID='ST_STBPRP_B' and FieldID='STTP' and Field=STTP) as STTP, (select FieldName from WRP_FieldInfo where FormID='ST_STBPRP_B' and FieldID='STGR' and Field=STGR) as STGR, (select stuff((select ',' + FieldName from WRP_FieldInfo where FormID='ST_STBPRP_F' and FieldID='TYPE' and Field in(select type from ST_STBPRP_F f where F.STCD=B.STCD) for xml path('')),1,1,'')) as THYPE, ESSTDT,DLOG,ADMAUTH, (select case ad_lv when 5 then AD_NM when 6 then (select AD_NM from WRP_AD_B sub where sub.ad_cd=m.UP_ADCD)+AD_NM end from WRP_AD_B m where AD_CD=ADDVCD) as AD_NM, ROW_NUMBER()OVER(ORDER BY ID) as RowId from ST_STBPRP_B b
STNM like '%'+#{STNM}+'%'
and
(b.addvcd like #{ADDVCD}+'%')
and b.STTP=#{STTP}
and b.STGR=#{STGR}
and b.STCD in(select STCD from ST_STBPRP_F where TYPE=#{TYPE})
)b
