select * from (select b.STCD,b.stnm,c.p,h.*, (select FieldName from WRP_FieldInfo where FormID='ST_STBPRP_B' and FieldID='STGR' and Field=STGR) as STGR, (select stuff((select ',' + canal_name from WRP_IrrBTCanalSystem where Canal_Code in(select baseid from ST_STINFO_WRP w where FormID='WRP_IrrBTCanalSystem' and w.stcd=b.stcd) for xml path('')),1,1,'')) as canal_name, (select case ad_lv when 5 then AD_NM when 6 then (select AD_NM from WRP_AD_B sub where sub.ad_cd=m.UP_ADCD)+AD_NM end from WRP_AD_B m where AD_CD=ADDVCD) as AD_NM ,ROW_NUMBER() OVER (ORDER BY b.wzpx) as RowId from ST_STBPRP_B b inner join ST_STBPRP_F f on f.type=1 and b.stcd=f.stcd left join (select stcd,p from ST_DP_C where TM='${TM}' ) as c on b.stcd=c.STCD left join (select * from (select STCD as temp,P,convert(varchar(2),TM)+':00-'+convert(varchar(2),(IIF(TM=23,0,TM+1)))+':00' as TM from ST_HP_C where DATEADD(hh,TM,CONVERT(varchar(100), DT, 111))>='${Time_min}' and DATEADD(hh,TM,CONVERT(varchar(100), DT, 111))<='${Time_max}') b PIVOT(MAX(P) FOR TM IN ([8:00-9:00],[9:00-10:00],[10:00-11:00],[11:00-12:00],[12:00-13:00],[13:00-14:00],[14:00-15:00],[15:00-16:00],[16:00-17:00],[17:00-18:00],[18:00-19:00],[19:00-20:00],[20:00-21:00],[21:00-22:00],[22:00-23:00],[23:00-0:00],[0:00-1:00],[1:00-2:00],[2:00-3:00],[3:00-4:00],[4:00-5:00],[5:00-6:00],[6:00-7:00],[7:00-8:00]) ) a) h on b.STCD=h.temp
b.stnm like '%'+#{STNM}+'%'
and
(b.addvcd like #{ADDVCD}+'%')
and b.stcd in(select stcd from ST_STINFO_WRP where FormID='WRP_IrrBTCanalSystem' and (BaseID = #{Canal_Code} or BaseID in(select Canal_Code from dbo.fn_getCanalChild(#{Canal_Code}))))
and stgr = #{STGR}
and p >= #{P}
)b where RowId between (#{_page}-1)*#{_page_size} and #{_page}*#{_page_size}

