mapper.select:
select * from (SELECT CONVERT(VARCHAR(13),a.dt,120) AS dt, b.*, ROW_NUMBER() OVER(Order by a.dt desc) AS RowId FROM ( SELECT dateadd(HH, number,'${Time_min}') dt FROM master..spt_values WHERE type = 'p' AND dateadd(HH, number, '${Time_min}') <='${Time_max}' ) a LEFT JOIN (SELECT
max(CASE b.stnm WHEN '${item}' THEN isnull(RZ,0) ELSE 0 end)${item}平均水位, max(CASE b.stnm WHEN '${item}' THEN isnull(CV,0) ELSE 0 end)${item}水势, max(CASE b.stnm WHEN '${item}' THEN isnull(INQ,0) ELSE 0 end)${item}平均入口流量, max(CASE b.stnm WHEN '${item}' THEN isnull(OTQ,0) ELSE 0 end)${item}平均出口流量, max(CASE b.stnm WHEN '${item}' THEN isnull(HW,0) ELSE 0 end)${item}小时蓄水量,
DATEADD(hh,TM,CONVERT(varchar(100), DT, 111)) as temp FROM ST_RSVR_H h inner join ST_STBPRP_B b on h.STCD=b.STCD where DATEADD(hh,TM,CONVERT(varchar(100), DT, 111))>= '${Time_min}' and DATEADD(hh,TM,CONVERT(varchar(100), DT, 111))<='${Time_max}' GROUP BY dt,tm ) b ON a.dt = b.temp ) as b where RowId between 1 and 999999999;
